let swiperImg=new Swiper(".swiper-container",{loop:!1,speed:2400,spaceBetween:"2%",autoplay:{delay:6e3},on:{init:function(){updateCounter(this)},slideChange:function(){updateCounter(this)}},scrollbar:{el:".swiper-scrollbar",draggable:!0},navigation:{prevEl:".swiper-button-prev",nextEl:".swiper-button-next"}}),swiperContainer=document.querySelector(".swiper-container"),observer=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting?swiperImg.autoplay.start():swiperImg.autoplay.stop()})},{threshold:0});function updateCounter(e){var t=document.querySelector(".hero__current-count"),n=document.querySelector(".hero__total-count"),o=e.realIndex+1;t.textContent=o<10?"0"+o:o,n.textContent=e.slides.length<10?"0"+e.slides.length:e.slides.length}let mobileNav=document.querySelector(".header__mobile-navigation"),mobileMenu=document.querySelector(".mobile-menu");mobileNav.addEventListener("click",()=>{var e;mobileMenu.classList.contains("open")?(mobileMenu.computedStyleMap.height="0",mobileMenu.classList.remove("open"),setTimeout(()=>{mobileMenu.style.height=""},0)):(mobileMenu.style.height="0",mobileMenu.classList.add("open"),e=mobileMenu.scrollHeight+"px",mobileMenu.style.height=e)});